import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import {
  VALIDATOR_REQUIRE,
  VALIDATOR_MINLENGTH,
} from "../../shared/util/validators";
import { useForm } from "../../shared/hooks/form-hook";
import "./NewPlace.css";
import Card from "../../shared/components/UIElements/Card";

const DUMMY_PLACES = [
  {
    id: 1,
    title: "Gunung Lawu",
    image:
      "data:image/jpeg;base64,",
    address: "Gunung di jawa tengah",
    description:
      "Mount Lawu, or Gunung Lawu, is a massive compound stratovolcano straddling the border between East Java and Central Java, Indonesia.",
    location: {
      lat: -7.6274785,
      lng: 111.1854118,
    },
    creator_id: 1,
  },
  {
    id: 2,
    title: "Gunung Gede",
    image:
      "data:image/jpeg;base64,",
    address: "Gunung di indonesia",
    description:
      "Mount Gede or Gunung Gede is a stratovolcano in West Java, Indonesia.",
    location: {
      lat: -6.7877563,
      lng: 106.9731896,
    },
    creator_id: 2,
  },
  {
    id: 3,
    title: "Gunung Pangrango",
    image:
      "data:image/jpeg;base64,",
    address: "Gunung di jawa",
    description:
      "Mount Pangrango is a dormant stratovolcano located in the Sunda Arc of West Java, Indonesia.",
    location: {
      lat: -6.7699996,
      lng: 106.9461015,
    },
    creator_id: 3,
  },
];

const UpdatePlace = () => {
  const [isLoading, setIsLoading] = useState(true);
  const placeId = useParams().placeId;

  const [formState, inputHandler, setFormData] = useForm(
    {
      title: {
        value: "",
        isValid: false,
      },
      description: {
        value: "",
        isValid: false,
      },
    },
    false
  );

  const identifiedPlace = DUMMY_PLACES.find((p) => p.id === parseInt(placeId));

  useEffect(() => {
    if (identifiedPlace) {
      setFormData(
        {
          title: {
            value: identifiedPlace.title,
            isValid: true,
          },
          description: {
            value: identifiedPlace.description,
            isValid: true,
          },
        },
        true
      );
    }
    setIsLoading(false);
  }, [setFormData, identifiedPlace]);

  const placeUpdateSubmitHandler = (event) => {
    event.preventDefault();
    console.log(formState.inputs);
  };

  if (!identifiedPlace) {
    return (
      <div className="center">
        <Card>
          <h2>Could not find place!</h2>
        </Card>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="center">
        <h2>Loading...</h2>
      </div>
    );
  }

  return (
    <form className="place-form" onSubmit={placeUpdateSubmitHandler}>
      <Input
        id="title"
        element="input"
        type="text"
        label="Title"
        validators={[VALIDATOR_REQUIRE()]}
        errorText="Please enter a valid title."
        onInput={inputHandler}
        initialValue={formState.inputs.title.value}
        initialValid={formState.inputs.title.isValid}
      />
      <Input
        id="description"
        element="textarea"
        label="Description"
        validators={[VALIDATOR_MINLENGTH(5)]}
        errorText="Please enter a valid description (min. 5 characters)."
        onInput={inputHandler}
        initialValue={formState.inputs.description.value}
        initialValid={formState.inputs.description.isValid}
      />
      <Button type="submit" disabled={!formState.isValid}>
        UPDATE PLACE
      </Button>
    </form>
  );
};

export default UpdatePlace;
